import uk.ac.ucl.cs.mr.acltutorial.ManualMF._
import uk.ac.ucl.cs.mr.acltutorial.MatrixRenderer._
import uk.ac.ucl.cs.mr.acltutorial.Renderer._
import cc.factorie.la.{DenseTensor2, DenseTensor1}
import ml.wolfe.{Mat, Vect}
val layout = Layout(cw = 80, ch = 50, colHeaderSize = 150, rowHeaderSize=100)

def optimizeL2(M: Mat, K: Int, iters: Int, alpha:Double = 0.1, initScale:Double = 1.0): (Seq[Vect], Seq[Vect]) = {
  val rand = new scala.util.Random(0)
  val AV = initialAV(K, M.dim1, M.dim2, initScale)
  val A = AV._1; val V = AV._2
  def update(i: Int, j: Int) = {
    val a = A(i).copy
    val v = V(j).copy
    val y = a dot v
    A(i) += v * alpha * (M(i, j) - y)
    V(j) += a * alpha * (M(i, j) - y)
  }
  for (i <- Range(0,iters).toList) {
    update(rand.nextInt(M.dim1), 
           rand.nextInt(M.dim2))
  }
  (A, V)
}

